import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';
import './SignUpPage.css';

function SignUpPage() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [mobile, setMobile] = useState('');

    const handleSignUp = () => {
        if (!name || !email || !password || !mobile) {
            alert('Please fill in all fields.');
            return;
        }
        // Handle sign up
        axios.post('/api/users/signup', { name, email, password, mobile })
            .then(response => {
                console.log('Signed up:', response.data);
                alert('Signup successful! Please log in.');
            })
            .catch(error => {
                console.error('Error signing up:', error);
                alert('Error signing up. Please try again.');
            });
    };

    return (
        <div className="signup-page">
            <NavBar />
            <div className="signup-header">
                <h1>Log in to access your account</h1>
                <div className="login-options">
                    <Link to="/login" className="login-option">
                        <div className="login-option-content">
                            <h2>Driver</h2>
                            <span>→</span>
                        </div>
                    </Link>
                    <Link to="/login" className="login-option">
                        <div className="login-option-content">
                            <h2>Rider</h2>
                            <span>→</span>
                        </div>
                    </Link>
                </div>
            </div>
            <div className="card">
                <h1>Create an Uber Account</h1>
                <p>Sign up to start riding or driving with Uber.</p>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={e => setName(e.target.value)}
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Mobile Number"
                    value={mobile}
                    onChange={e => setMobile(e.target.value)}
                />
                <p>By signing up, you agree to Uber's <Link to="/terms">Terms and Conditions</Link>.</p>
                <button className="button" onClick={handleSignUp}>Sign Up</button>
            </div>
            <Footer />
        </div>
    );
}

export default SignUpPage;
