import React, { useState } from 'react';
import axios from 'axios';
import './BookingPage.css';

function BookingPage() {
    const [pickupLocation, setPickupLocation] = useState('');
    const [destination, setDestination] = useState('');
    const userId = localStorage.getItem('userId'); // Assume userId is stored in localStorage after login

    const handleBookRide = () => {
        // Handle booking a ride
        axios.post('/api/rides/book', { userId, pickupLocation, destination })
            .then(response => console.log('Ride booked:', response.data))
            .catch(error => console.error('Error booking ride:', error));
    };

    return (
        <div className="container">
            <div className="card">
                <h1>Booking Page</h1>
                <input
                    type="text"
                    placeholder="Pickup Location"
                    value={pickupLocation}
                    onChange={e => setPickupLocation(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Destination"
                    value={destination}
                    onChange={e => setDestination(e.target.value)}
                />
                <button className="button" onClick={handleBookRide}>Book Ride</button>
            </div>
        </div>
    );
}

export default BookingPage;
