import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './NavBar.css'; // Import styles for NavBar

function NavBar() {
    const location = useLocation();

    // Define pages where the navbar should not be displayed
    const noNavBarPages = ['/login', '/signup'];

    // If the current page is in the noNavBarPages list, do not render the navbar
    if (noNavBarPages.includes(location.pathname)) {
        return null;
    }

    return (
        <nav className="navbar">
            <div className="navbar-logo">
                <Link to="/">Uber 1.0</Link>
            </div>
            <ul className="navbar-links">
                {location.pathname === '/' && (
                    <>
                        <li><Link to="/ride">Ride</Link></li>
                        <li><Link to="/drive">Drive</Link></li>
                        <li><Link to="/business">Business</Link></li>
                        <li><Link to="/help">Help</Link></li>
                    </>
                )}
                {location.pathname === '/profile' && (
                    <>
                        <li><Link to="/ride-history">Ride History</Link></li>
                        <li><Link to="/settings">Settings</Link></li>
                    </>
                )}
                {location.pathname === '/admin-dashboard' && (
                    <>
                        <li><Link to="/fare-management">Fare Management</Link></li>
                        <li><Link to="/driver-rating">Driver Rating</Link></li>
                        <li><Link to="/schedule-rides">Schedule Rides</Link></li>
                    </>
                )}
                {location.pathname === '/booking' && (
                    <>
                        <li><Link to="/vehicle-selection">Vehicle Selection</Link></li>
                        <li><Link to="/uber-pool">Uber Pool</Link></li>
                        <li><Link to="/fare-calculator">Fare Calculator</Link></li>
                    </>
                )}
                {location.pathname === '/support' && (
                    <>
                        <li><Link to="/customer-support">Customer Support</Link></li>
                        <li><Link to="/emergency-assistance">Emergency Assistance</Link></li>
                        <li><Link to="/trusted-contacts">Trusted Contacts</Link></li>
                    </>
                )}
            </ul>
        </nav>
    );
}

export default NavBar;
