import React, { useState } from 'react';
import axios from 'axios';
import './ScheduleRidesPage.css';

function ScheduleRidesPage() {
    const [pickupLocation, setPickupLocation] = useState('');
    const [destination, setDestination] = useState('');
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');

    const handleScheduleRide = () => {
        // Handle scheduling a ride
        axios.post('/api/rides/schedule', { pickupLocation, destination, date, time })
            .then(response => console.log('Ride scheduled:', response.data))
            .catch(error => console.error('Error scheduling ride:', error));
    };

    return (
        <div className="container">
            <div className="card">
                <h1>Schedule Rides Page</h1>
                <input
                    type="text"
                    placeholder="Pickup Location"
                    value={pickupLocation}
                    onChange={e => setPickupLocation(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Destination"
                    value={destination}
                    onChange={e => setDestination(e.target.value)}
                />
                <input
                    type="date"
                    value={date}
                    onChange={e => setDate(e.target.value)}
                />
                <input
                    type="time"
                    value={time}
                    onChange={e => setTime(e.target.value)}
                />
                <button className="button" onClick={handleScheduleRide}>Schedule Ride</button>
            </div>
        </div>
    );
}

export default ScheduleRidesPage;
