import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom'; // Use useHistory instead of useNavigate
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';
import './HomePage.css';
import '../styles/ButtonStyles.css'; // Import button styles
import axios from 'axios';

function HomePage() {
    const [sections, setSections] = useState([]);
    const history = useHistory(); // Initialize useHistory

    useEffect(() => {
        // Fetch section details from the backend
        const fetchSections = async () => {
            try {
                const response = await axios.get('/api/sections');
                setSections(response.data);
            } catch (error) {
                console.error('Error fetching sections:', error);
            }
        };

        fetchSections();
    }, []);

    const handleButtonClick = (route) => {
        history.push(route);
    };

    return (
        <div className="home-page">
            <NavBar />
            <div className="hero-section">
                <h1>Welcome to Uber</h1>
                <p>Explore our services and features below.</p>
            </div>
            <div className="sections">
                {sections.map((section) => (
                    <div key={section._id} className="section-card">
                        <h2>{section.title}</h2>
                        <p>{section.description}</p>
                        <button
                            className="button"
                            onClick={() => handleButtonClick(section.route)}
                        >
                            {section.buttonText}
                        </button>
                    </div>
                ))}
            </div>
            <Footer />
        </div>
    );
}

export default HomePage;
