import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProfilePage.css';

function ProfilePage() {
    const [profile, setProfile] = useState({});
    const userId = localStorage.getItem('userId'); // Assume userId is stored in localStorage after login

    useEffect(() => {
        // Fetch profile details from the backend
        axios.get(`/api/users/profile/${userId}`)
            .then(response => setProfile(response.data))
            .catch(error => console.error('Error fetching profile:', error));
    }, [userId]);

    const handleEdit = () => {
        // Handle profile edit functionality
        console.log('Edit profile functionality not implemented yet.');
    };

    return (
        <div className="container">
            <div className="card">
                <h1>Profile Page</h1>
                <p>Name: {profile.name}</p>
                <p>Email: {profile.email}</p>
                <p>Mobile: {profile.mobile}</p>
                <button className="button" onClick={handleEdit}>Edit Profile</button>
            </div>
        </div>
    );
}

export default ProfilePage;
