import React, { useState } from 'react';
import axios from 'axios';
import './BookingPage.css';

function BookingPage() {
    const [pickupLocation, setPickupLocation] = useState('');
    const [destination, setDestination] = useState('');
    const [message, setMessage] = useState('');
    const userId = localStorage.getItem('userId'); // Assume userId is stored in localStorage after login

    const handleBookRide = () => {
        if (!pickupLocation || !destination) {
            setMessage('Please fill in both pickup and destination locations.');
            return;
        }
        // Handle booking a ride
        axios.post('/api/rides/book', { userId, pickupLocation, destination })
            .then(response => setMessage('Ride booked successfully!'))
            .catch(error => setMessage('Error booking ride.'));
    };

    return (
        <div className="container">
            <div className="card">
                <h1>Booking Page</h1>
                <input
                    type="text"
                    placeholder="Pickup Location"
                    value={pickupLocation}
                    onChange={e => setPickupLocation(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Destination"
                    value={destination}
                    onChange={e => setDestination(e.target.value)}
                />
                <button className="button" onClick={handleBookRide}>Book Ride</button>
                {message && <p>{message}</p>}
            </div>
        </div>
    );
}

export default BookingPage;
