import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';
import './HomePage.css';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';

function HomePage() {
    const [pickupLocation, setPickupLocation] = useState('');
    const [dropoffLocation, setDropoffLocation] = useState('');
    const [pickupSuggestions, setPickupSuggestions] = useState([]);
    const [dropoffSuggestions, setDropoffSuggestions] = useState([]);
    const [mapCenter, setMapCenter] = useState([12.9716, 77.5946]); // Default to Bengaluru
    const [userLocation, setUserLocation] = useState(null);
    const [activeToggle, setActiveToggle] = useState('Ride');

    useEffect(() => {
        // Get user's current location
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;
                setUserLocation([latitude, longitude]);
                setMapCenter([latitude, longitude]); // Center map on user's location
            },
            (error) => {
                console.error('Error fetching user location:', error);
            }
        );
    }, []);

    const fetchSuggestions = async (query, setSuggestions) => {
        if (!query) {
            setSuggestions([]);
            return;
        }

        try {
            const params = { query };
            if (userLocation) {
                params.lat = userLocation[0];
                params.lon = userLocation[1];
            }

            const response = await axios.get('/api/locations/autocomplete', { params });
            setSuggestions(response.data.suggestions);
        } catch (error) {
            console.error('Error fetching address suggestions:', error);
        }
    };

    const handleSelectSuggestion = (location, lat, lon, setLocation, setSuggestions) => {
        setLocation(location);
        setSuggestions([]);
        setMapCenter([parseFloat(lat), parseFloat(lon)]);
    };

    return (
        <div className="home-page">
            <NavBar />
            <div className="hero-section">
                <div className="hero-content">
                    <h1>Go anywhere with Uber</h1>
                    <div className="ride-courier-toggle">
                        <button
                            className={`toggle-button ${activeToggle === 'Ride' ? 'active' : ''}`}
                            onClick={() => setActiveToggle('Ride')}
                        >
                            Ride
                        </button>
                        <button
                            className={`toggle-button ${activeToggle === 'Courier' ? 'active' : ''}`}
                            onClick={() => setActiveToggle('Courier')}
                        >
                            Courier
                        </button>
                    </div>
                    <div className="input-group">
                        <input
                            type="text"
                            placeholder="Pickup location"
                            value={pickupLocation}
                            onChange={e => {
                                setPickupLocation(e.target.value);
                                fetchSuggestions(e.target.value, setPickupSuggestions);
                            }}
                        />
                        {pickupSuggestions.length > 0 && (
                            <ul className="suggestions">
                                {pickupSuggestions.map((suggestion, index) => (
                                    <li
                                        key={index}
                                        onClick={() =>
                                            handleSelectSuggestion(
                                                suggestion.displayName,
                                                suggestion.lat,
                                                suggestion.lon,
                                                setPickupLocation,
                                                setPickupSuggestions
                                            )
                                        }
                                    >
                                        {suggestion.displayName}
                                    </li>
                                ))}
                            </ul>
                        )}
                        <input
                            type="text"
                            placeholder="Dropoff location"
                            value={dropoffLocation}
                            onChange={e => {
                                setDropoffLocation(e.target.value);
                                fetchSuggestions(e.target.value, setDropoffSuggestions);
                            }}
                        />
                        {dropoffSuggestions.length > 0 && (
                            <ul className="suggestions">
                                {dropoffSuggestions.map((suggestion, index) => (
                                    <li
                                        key={index}
                                        onClick={() =>
                                            handleSelectSuggestion(
                                                suggestion.displayName,
                                                suggestion.lat,
                                                suggestion.lon,
                                                setDropoffLocation,
                                                setDropoffSuggestions
                                            )
                                        }
                                    >
                                        {suggestion.displayName}
                                    </li>
                                ))}
                            </ul>
                        )}
                        <div className="date-time-group">
                            <input type="date" />
                            <input type="time" />
                        </div>
                        <button className="button">See prices</button>
                    </div>
                    <p>Log in to see your recent activity</p>
                </div>
                <div className="hero-map">
                    <MapContainer center={mapCenter} zoom={13} style={{ height: '100%', width: '100%' }}>
                        <TileLayer
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        />
                        <Marker position={mapCenter}>
                            <Popup>Selected Location</Popup>
                        </Marker>
                    </MapContainer>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default HomePage;
