import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './RiderLoginPage.css';

function RiderLoginPage() {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [mobileNumber, setMobileNumber] = useState('');
    const history = useHistory();

    const handleNext = () => {
        // Handle next action, e.g., form validation and submission
        console.log('First Name:', firstName);
        console.log('Last Name:', lastName);
        console.log('Mobile Number:', mobileNumber);
        // Redirect to the next page or perform other actions
    };

    return (
        <div className="rider-login-page">
            <h2>Confirm your information</h2>
            <div className="input-group">
                <input
                    type="text"
                    placeholder="Enter first name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Enter last name"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
            </div>
            <div className="input-group">
                <div className="country-code">
                    <img src="https://upload.wikimedia.org/wikipedia/en/4/41/Flag_of_India.svg" alt="India Flag" />
                    <span>+91</span>
                </div>
                <input
                    type="text"
                    placeholder="Mobile number"
                    value={mobileNumber}
                    onChange={(e) => setMobileNumber(e.target.value)}
                />
            </div>
            <div className="button-group">
                <button className="button back-button" onClick={() => history.goBack()}>←</button>
                <button className="button next-button" onClick={handleNext}>Next →</button>
            </div>
        </div>
    );
}

export default RiderLoginPage;
