import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RideHistoryPage.css';

function RideHistoryPage() {
    const [rideHistory, setRideHistory] = useState([]);
    const userId = localStorage.getItem('userId'); // Assume userId is stored in localStorage after login

    useEffect(() => {
        // Fetch ride history from the backend
        axios.get(`/api/rides/history/${userId}`)
            .then(response => setRideHistory(response.data))
            .catch(error => console.error('Error fetching ride history:', error));
    }, [userId]);

    return (
        <div className="container">
            <div className="card">
                <h1>Ride History Page</h1>
                <ul>
                    {rideHistory.map(ride => (
                        <li key={ride._id}>
                            <p>Date: {new Date(ride.createdAt).toLocaleString()}</p>
                            <p>Pickup: {ride.pickupLocation}</p>
                            <p>Destination: {ride.destination}</p>
                            <p>Status: {ride.status}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default RideHistoryPage;
